    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        int valorUnitario1 = Integer.parseInt(txtValorUnitario1.getText());
        int cantidad1 = Integer.parseInt(txtCantidad1.getText());
        int valorTotal1 = valorUnitario1 * cantidad1;
        
        txtValorTotal1.setText(String.valueOf(valorTotal1));
        
        int valorUnitario2 = Integer.parseInt(txtValorUnitario2.getText());
        int cantidad2 = Integer.parseInt(txtCantidad2.getText());
        int valorTotal2 = valorUnitario2 * cantidad2;
        
        txtValorTotal2.setText(String.valueOf(valorTotal2));
        
        int valorUnitario3 = Integer.parseInt(txtValorUnitario3.getText());
        int cantidad3 = Integer.parseInt(txtCantidad3.getText());
        int valorTotal3 = valorUnitario3 * cantidad3;
        
        txtValorTotal3.setText(String.valueOf(valorTotal3));
        
        int valorTotalFinal = valorTotal1 + valorTotal2 + valorTotal3;
        
        txtValorTotalFinal.setText(String.valueOf(valorTotalFinal));
        
        lblResultadoAviso.setText("Se√±or(a) " + txtCliente.getText()  +  " el total de su compra es: " + String.valueOf(valorTotalFinal));
    }//GEN-LAST:event_btnCalcularActionPerformed

